function resetStatus(){document.getElementById("title").innerText="INSERT INPUT",document.getElementById("receipt").style.display="none",document.getElementById("input-area").value="",document.getElementById("input-area").style.display="block",document.getElementById("print-button").style.display="block",document.getElementById("print-button").textContent="PRINT RECEIPT"}function receiptStatus(){document.getElementById("title").innerText="RECEIPT DETAILS",document.getElementById("receipt").style.display="block",document.getElementById("input-area").style.display="none",document.getElementById("print-button").textContent="CANCEL"}function getInput(t){let e=Array("2 book at 12.49\n1 music CD at 14.99\n1 chocolate bar at 0.85","1 imported box of chocolates at 10.00\n1 imported bottle of perfume at 47.50","1 imported bottle of perfume at 27.99\n1 bottle of perfume at 18.99\n1 packet of headache pills at 9.75\n3 box of imported chocolates at 11.25");resetStatus(),document.getElementById("input-area").value=e[t.textContent.split(" ")[1]-1]}function printReceipt(t){let e=document.getElementById("input-area").value;t.textContent.includes("CANCEL")?resetStatus():fetch("http://127.0.0.1:5000/api/v1/receipts",{method:"POST",headers:{Accept:"text/plain","Content-Type":"text/plain"},body:e}).then((t=>t.text())).then((t=>{document.getElementById("receipt").innerText=t,receiptStatus()}))}document.addEventListener("DOMContentLoaded",(t=>{resetStatus()}));
